sys_script_email.script:
(function runMailScript( /* GlideRecord */ current, /* TemplatePrinter */ template,
    /* Optional EmailOutbound */
    email, /* Optional GlideRecord */ email_action,
    /* Optional GlideRecord */
    event) {

    // Add your code here
    var rightNow = new GlideDateTime();

    var overDueCount = new GlideRecord('x_58872_needit_needit_task');
    overDueCount.addQuery('due_date', '<=', rightNow);
    overDueCount.addQuery('state', '<', 3);
    overDueCount.addQuery('assigned_to', current.assigned_to);

    overDueCount.query();
    if (overDueCount.getRowCount() > 1) {
        template.print("In addition to this overdue NeedIt Task, you have " + (overDueCount.getRowCount() - 1) + " more overdue tasks.");
    }
})(current, template, email, email_action, event);
